def exec_from_buffer():
    global buffer
    
    while "\n" in buffer:
        end_point_index = buffer.find("\n")
        keycodes_to_exec = buffer[:end_point_index].split(",")
        for keycode in keycodes_to_exec:
            if keycode == "0":
                kbd.release_all()
                continue
            if keycode.isdigit():
                print(keycode)
                try:
                    kbd.press(int(keycode))
                    print(keycode)
                except OverflowError:
                    print("overflow")
        
        buffer = buffer[end_point_index+1:]
    kbd.release_all()
while True:
    if uart.in_waiting > 0:
        
        received_bytes = uart.read(uart.in_waiting)
        try:
            strings = received_bytes.decode("utf-8")
        except UnicodeError:
            continue
        buffer += strings
        exec_from_buffer()
        